mapper.update:
update shift_work
set start_time = #{startTime},
end_time = #{endTime}
where shift_work_id = #{shiftWorkId}

mapper.select:
select count(shift_work_id) from shift_work where
shift_work_id =
#{shiftWorkId}

select
count(shift_work_id) from pre_schedule where
employee_id = #{employeeId} and apply_date = #{applyDate}
